mapper.update:


    UPDATE blog
	set blogTitle = #{blogTitle}
	   ,logoFile = #{logoFile}
	where id = #{id}

mapper.delete:


     DELETE FROM category 
     WHERE id = #{id}
	 and cateNo = #{cateNo}

mapper.select:
SELECT p.postno ,p.cateno ,c.cateName ,p.posttitle ,p.postcontent ,to_char(p.regdate, 'yyyy/mm/dd') regDate FROM blog b,category c,post p where b.id = c.id and c.cateno = p.cateno and b.id = #{id} and p.postno =
(SELECT MAX(p.postno) FROM blog b,category c,post p where b.id = c.id and c.cateno = p.cateno and b.id = #{id})
#{post}
